import React from 'react';
import { NavLink, useLocation, Outlet } from 'react-router-dom';

const navItems = [
  { to: '/dashboard', label: 'Dashboard', icon: '🏠' },
  { to: '/pelaporan', label: 'Reporting', icon: '📝' },
  { to: '/counseling', label: 'Counseling', icon: '💬' },
  { to: '/admin', label: 'Admin', icon: '📩' },
  { to: '/legal', label: 'Legal Center', icon: '⚖️' },
  { to: '/team', label: 'Team', icon: '👥' },
];

const Layout = () => {
  const location = useLocation();

  return (
    <div
      style={{
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        backgroundColor: 'white',
        color: '#4B3A26',
        fontFamily: 'Arial, sans-serif',
      }}
    >
      <nav
        style={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          backgroundColor: '#4B3A26',
          padding: '1rem',
          gap: '2rem',
          flexWrap: 'wrap',
        }}
      >
        {/* Kelompok kiri (3 item pertama) */}
        {navItems.slice(0, 3).map(({ to, label, icon }) => {
          const isActive = location.pathname === to;
          return (
            <NavLink
              key={to}
              to={to}
              style={{
                color: isActive ? 'white' : '#b0a68f',
                textDecoration: 'none',
                display: 'flex',
                alignItems: 'center',
                fontSize: '1rem',
                fontWeight: 'bold',
                transition: 'color 0.3s',
                gap: '0.5rem',
              }}
              end
            >
              <div style={{ fontSize: '1.25rem' }}>{icon}</div>
              {label}
            </NavLink>
          );
        })}

        {/* Garis vertikal pemisah */}
        <div
          style={{
            width: '1px',
            height: '20px',
            backgroundColor: '#b0a68f',
          }}
        />

        {/* Kelompok kanan (3 item terakhir) */}
        {navItems.slice(3).map(({ to, label, icon }) => {
          const isActive = location.pathname === to;
          return (
            <NavLink
              key={to}
              to={to}
              style={{
                color: isActive ? 'white' : '#b0a68f',
                textDecoration: 'none',
                display: 'flex',
                alignItems: 'center',
                fontSize: '1rem',
                fontWeight: 'bold',
                transition: 'color 0.3s',
                gap: '0.5rem',
              }}
              end
            >
              <div style={{ fontSize: '1.25rem' }}>{icon}</div>
              {label}
            </NavLink>
          );
        })}
      </nav>

      <main
        style={{
          flex: 1,
          padding: '2rem',
          maxWidth: '960px',
          margin: '0 auto',
        }}
      >
        <Outlet /> {/* Komponen anak akan dirender di sini */}
      </main>
    </div>
  );
};

export default Layout;
