import React from 'react';

const LegalCenter = () => {
  return (
    <div style={{ padding: '1rem' }}>
      <h1>Pusat Informasi Hukum (Legal Center)</h1>
      <section>
        <h2>Informasi Hukum</h2>
        <ul>
          <li>UU No. 35 Tahun 2014 (Perlindungan Anak)</li>
          <li>UU No. 20 Tahun 2003 (Sistem Pendidikan Nasional)</li>
          <li>Permendikbudristek No. 46 Tahun 2023 (Pencegahan dan Penanganan Kekerasan di Satuan Pendidikan)</li>
        </ul>
      </section>
      <section>
        <h2>Infografis & Video Singkat</h2>
        <p><i>Infografis dan video singkat tentang hak-hak siswa akan ditampilkan di sini.</i></p>
      </section>
      <section>
        <h2>FAQ</h2>
        <p><b>Apa yang harus saya lakukan jika hak saya dilanggar?</b></p>
        <p>Jika hak Anda dilanggar, segera laporkan ke pihak sekolah atau konselor dan cari bantuan hukum jika diperlukan.</p>
      </section>
    </div>
  );
};

export default LegalCenter;
