import React, { useState } from 'react';

const laws = [
  {
    id: 1,
    title: 'UU No. 35 Tahun 2014 (Perlindungan Anak)',
    content: "Undang-undang (UU) Nomor 35 Tahun 2014 tentang Perubahan atas Undang-Undang Nomor 23 Tahun 2002 Tentang Perlindungan Anak. Hal ini mempertegas tentang perlunya pemberatan sanksi pidana dan denda bagi pelaku kejahatan terhadap Anak, untuk memberikan efek jera, serta mendorong adanya langkah konkret untuk memulihkan kembali fisik, psikis dan sosial Anak korban dan/atau Anak pelaku kejahatan. Hal tersebut perlu dilakukan untuk mengantisipasi Anak korban dan/atau Anak pelaku kejahatan di kemudian hari tidak menjadi pelaku kejahatan yang sama.",
  },
  {
    id: 2,
    title: 'UU No. 20 Tahun 2003 (Sistem Pendidikan Nasional)',
    content: "Dalam UU ini diatur mengenai dasar, fungsi, dan tujuan sistem pendidikan nasional; prinsip penyelenggaraan pendidikan; hak dan kewajiban warga negara, orang tua, masyarakat, dan pemerintah; peserta didik; jalur, jenjang, dan jenis pendidikan; bahasa pengantar; dan wajib belajar. Selain itu diatur juga mengenai standar nasional pendidikan; kurikulum; pendidik dan tenaga kependidikan; sarana dan prasarana pendidikan; pendanaan pendidikan; pengelolaan pendidikan; peran serta masyarakat dalam pendidikan; evaluasi, akreditasi, dan sertifikasi; pendirian satuan pendidikan; penyelenggaraan pendidikan oleh lembaga negara lain; pengawasan; dan ketentuan pidana.",
  },
  {
    id: 3,
    title: 'Permendikbudristek No. 46 Tahun 2023 (Pencegahan dan Penanganan Kekerasan di Satuan Pendidikan)',
    content: "Dalam Peraturan Menteri ini diatur tentang sasaran pencegahan dan penangggulangan kekerasan meliputi Peserta Didik, Pendidik, Tenaga Kependidikan, orang tua/wali, Komite Sekolah; dan Masyarakat.",
  },
];

<section style={{ marginTop: '2rem' }}>
  <h2>Hak-Hak Siswa dalam Infografis</h2>
  <div style={{
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
    gap: '1rem',
    marginTop: '1rem',
  }}>
    {[
      { icon: '🛡️', label: 'Aman dari Kekerasan' },
      { icon: '🗣️', label: 'Bebas Berpendapat' },
      { icon: '🔒', label: 'Privasi Dilindungi' },
      { icon: '🙅‍♀️', label: 'Anti Intimidasi' },
      { icon: '🤝', label: 'Ada Pendamping' },
      { icon: '⚖️', label: 'Perlakuan Setara' },
      { icon: '🚫', label: 'Tolak Pelecehan' },
      { icon: '🎓', label: 'Belajar Nyaman' },
    ].map((item, idx) => (
      <div key={idx} style={{
        border: '1px solid #ddd',
        borderRadius: '10px',
        padding: '1rem',
        textAlign: 'center',
        backgroundColor: '#fdfdfd',
        boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
        transition: 'transform 0.3s',
      }}>
        <div style={{ fontSize: '2rem' }}>{item.icon}</div>
        <div style={{ marginTop: '0.5rem', fontWeight: 'bold', color: '#333' }}>{item.label}</div>
      </div>
    ))}
  </div>
</section>

const videos = [
  {
    id: 1,
    title: 'Video Hak-Hak Siswa',
    videoUrl: 'https://www.youtube.com/embed/5MgBikgcWnY',
  },
];

const LegalCenter = () => {
  const [selectedLaw, setSelectedLaw] = useState(null);

  const handleLawClick = (law) => {
    setSelectedLaw(law);
  };

  return (
    <div style={{ padding: '1rem' }}>
      <h1>Pusat Informasi Hukum (Legal Center)</h1>
      <section>
        <h2>Informasi Hukum</h2>
        <ul style={{ listStyleType: 'none', paddingLeft: 0 }}>
          {laws.map((law) => (
            <li
              key={law.id}
              onClick={() => handleLawClick(law)}
              style={{
                cursor: 'pointer',
                color: '#4B3A26',
                marginBottom: '0.5rem',
                textDecoration: selectedLaw?.id === law.id ? 'underline' : 'none',
              }}
            >
              {law.title}
            </li>
          ))}
        </ul>
        {selectedLaw && (
          <div
            style={{
              border: '1px solid #4B3A26',
              padding: '1rem',
              marginTop: '1rem',
              backgroundColor: '#f9f9f9',
            }}
          >
            <h3>{selectedLaw.title}</h3>
            <p>{selectedLaw.content}</p>
          </div>
        )}
      </section>
      <section style={{ marginTop: '2rem' }}>
        <h2>Infografis & Video Singkat</h2>
        {infographics.map((info) => (
          <div key={info.id} style={{ marginBottom: '1rem' }}>
            <h3>{info.title}</h3>
            <img
              src={info.imgUrl}
              alt={info.title}
              style={{ maxWidth: '100%', height: 'auto', borderRadius: '8px' }}
            />
          </div>
        ))}
        {videos.map((vid) => (
          <div key={vid.id} style={{ marginBottom: '1rem' }}>
            <h3>{vid.title}</h3>
            <div style={{ position: 'relative', paddingBottom: '56.25%', height: 0, overflow: 'hidden' }}>
              <iframe
                src={vid.videoUrl}
                title={vid.title}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}
              ></iframe>
            </div>
          </div>
        ))}
      </section>
      <section style={{ marginTop: '2rem' }}>
        <h2>FAQ</h2>
        <p><b>Apa yang harus saya lakukan jika hak saya dilanggar?</b></p>
        <p>Jika hak Anda dilanggar, segera laporkan ke pihak sekolah atau konselor dan cari bantuan hukum jika diperlukan.</p>
      </section>
    </div>
  );
};

export default LegalCenter;
