import React, { useState } from 'react';

const laws = [
  {
    id: 1,
    title: 'UU No. 35 Tahun 2014 (Perlindungan Anak)',
    content: "Undang-undang (UU) Nomor 35 Tahun 2014 tentang Perubahan atas Undang-Undang Nomor 23 Tahun 2002 Tentang Perlindungan Anak ",
  },
  {
    id: 2,
    title: 'UU No. 20 Tahun 2003 (Sistem Pendidikan Nasional)',
    content: "UU No. 20 Tahun 2003 mengatur sistem pendidikan nasional yang mencakup tujuan, prinsip, dan pelaksanaan pendidikan di Indonesia.",
  },
  {
    id: 3,
    title: 'Permendikbudristek No. 46 Tahun 2023 (Pencegahan dan Penanganan Kekerasan di Satuan Pendidikan)',
    content: "Permendikbudristek No. 46 Tahun 2023 mengatur langkah-langkah pencegahan dan penanganan kekerasan di lingkungan satuan pendidikan.",
  },
];

const infographics = [
  {
    id: 1,
    title: 'Hak-Hak Siswa Infografis',
    imgUrl: 'https://i.ibb.co/7QpKsCX/infographic-siswa.png',
  },
];

const videos = [
  {
    id: 1,
    title: 'Video Hak-Hak Siswa',
    videoUrl: 'https://www.youtube.com/embed/5MgBikgcWnY',
  },
];

const LegalCenter = () => {
  const [selectedLaw, setSelectedLaw] = useState(null);

  const handleLawClick = (law) => {
    setSelectedLaw(law);
  };

  return (
    <div style={{ padding: '1rem' }}>
      <h1>Pusat Informasi Hukum (Legal Center)</h1>
      <section>
        <h2>Informasi Hukum</h2>
        <ul style={{ listStyleType: 'none', paddingLeft: 0 }}>
          {laws.map((law) => (
            <li
              key={law.id}
              onClick={() => handleLawClick(law)}
              style={{
                cursor: 'pointer',
                color: '#4B3A26',
                marginBottom: '0.5rem',
                textDecoration: selectedLaw?.id === law.id ? 'underline' : 'none',
              }}
            >
              {law.title}
            </li>
          ))}
        </ul>
        {selectedLaw && (
          <div
            style={{
              border: '1px solid #4B3A26',
              padding: '1rem',
              marginTop: '1rem',
              backgroundColor: '#f9f9f9',
            }}
          >
            <h3>{selectedLaw.title}</h3>
            <p>{selectedLaw.content}</p>
          </div>
        )}
      </section>
      <section style={{ marginTop: '2rem' }}>
        <h2>Infografis & Video Singkat</h2>
        {infographics.map((info) => (
          <div key={info.id} style={{ marginBottom: '1rem' }}>
            <h3>{info.title}</h3>
            <img
              src={info.imgUrl}
              alt={info.title}
              style={{ maxWidth: '100%', height: 'auto', borderRadius: '8px' }}
            />
          </div>
        ))}
        {videos.map((vid) => (
          <div key={vid.id} style={{ marginBottom: '1rem' }}>
            <h3>{vid.title}</h3>
            <div style={{ position: 'relative', paddingBottom: '56.25%', height: 0, overflow: 'hidden' }}>
              <iframe
                src={vid.videoUrl}
                title={vid.title}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}
              ></iframe>
            </div>
          </div>
        ))}
      </section>
      <section style={{ marginTop: '2rem' }}>
        <h2>FAQ</h2>
        <p><b>Apa yang harus saya lakukan jika hak saya dilanggar?</b></p>
        <p>Jika hak Anda dilanggar, segera laporkan ke pihak sekolah atau konselor dan cari bantuan hukum jika diperlukan.</p>
      </section>
    </div>
  );
};

export default LegalCenter;
