import React, { useState } from 'react';

const laws = [
  {
    id: 1,
    title: 'UU No. 35 Tahun 2014 (Perlindungan Anak)',
    content:
      "Undang-undang ini mempertegas pemberatan sanksi pidana dan denda bagi pelaku kejahatan terhadap anak serta mendorong pemulihan fisik, psikis, dan sosial anak korban dan/atau pelaku.",
  },
  {
    id: 2,
    title: 'UU No. 20 Tahun 2003 (Sistem Pendidikan Nasional)',
    content:
      "UU ini mengatur prinsip penyelenggaraan pendidikan, hak dan kewajiban peserta didik, serta peran masyarakat dan pemerintah dalam sistem pendidikan nasional.",
  },
  {
    id: 3,
    title: 'Permendikbudristek No. 46 Tahun 2023',
    content:
      "Mengatur pencegahan dan penanganan kekerasan di lingkungan satuan pendidikan secara sistematis dan terintegrasi.",
  },
];

const videos = [
  {
    id: 1,
    title: 'Video Edukasi Tentang Hak-Hak Siswa',
    videoUrl: 'https://www.youtube.com/watch?v=5ceIhxUa-Fo',
  },
];

const infographicItems = [
  { icon: '🛡️', label: 'Aman dari Kekerasan' },
  { icon: '🗣️', label: 'Bebas Berpendapat' },
  { icon: '🔒', label: 'Privasi Dilindungi' },
  { icon: '🙅‍♀️', label: 'Anti Intimidasi' },
  { icon: '🤝', label: 'Ada Pendamping' },
  { icon: '⚖️', label: 'Perlakuan Setara' },
  { icon: '🚫', label: 'Tolak Pelecehan' },
  { icon: '🎓', label: 'Belajar Nyaman' },
];

const LegalCenter = () => {
  const [selectedLaw, setSelectedLaw] = useState(null);

  const handleLawClick = (law) => {
    setSelectedLaw(law);
  };

  return (
    <div style={{ padding: '1rem', fontFamily: 'Arial, sans-serif' }}>
      <h1>Pusat Informasi Hukum (Legal Center)</h1>

      {/* Informasi Hukum */}
      <section>
        <h2>Informasi Hukum</h2>
        <ul style={{ listStyleType: 'none', paddingLeft: 0 }}>
          {laws.map((law) => (
            <li
              key={law.id}
              onClick={() => handleLawClick(law)}
              style={{
                cursor: 'pointer',
                color: '#4B3A26',
                marginBottom: '0.5rem',
                textDecoration: selectedLaw?.id === law.id ? 'underline' : 'none',
              }}
            >
              {law.title}
            </li>
          ))}
        </ul>
        {selectedLaw && (
          <div
            style={{
              border: '1px solid #4B3A26',
              padding: '1rem',
              marginTop: '1rem',
              backgroundColor: '#f9f9f9',
            }}
          >
            <h3>{selectedLaw.title}</h3>
            <p>{selectedLaw.content}</p>
          </div>
        )}
      </section>

      {/* Infografis Buatan Sendiri */}
      <section style={{ marginTop: '2rem' }}>
        <h2>Infografis Hak-Hak Siswa</h2>
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(160px, 1fr))',
            gap: '1rem',
            marginTop: '1rem',
          }}
        >
          {infographicItems.map((item, idx) => (
            <div
              key={idx}
              style={{
                border: '1px solid #ccc',
                borderRadius: '10px',
                padding: '1rem',
                textAlign: 'center',
                backgroundColor: '#c6c5c5ff',
                boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
              }}
            >
              <div style={{ fontSize: '2rem' }}>{item.icon}</div>
              <div style={{ marginTop: '0.5rem', fontWeight: 'bold', color: '#333' }}>{item.label}</div>
            </div>
          ))}
        </div>
      </section>

      {/* Video */}
      <section style={{ marginTop: '2rem' }}>
        <h2>Video Edukasi</h2>
        {videos.map((vid) => (
          <div key={vid.id} style={{ marginBottom: '1rem' }}>
            <h3>{vid.title}</h3>
            <div
              style={{
                position: 'relative',
                paddingBottom: '56.25%',
                height: 0,
                overflow: 'hidden',
              }}
            >
              <iframe
                src={vid.videoUrl}
                title={vid.title}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  height: '100%',
                }}
              ></iframe>
            </div>
          </div>
        ))}
      </section>

      {/* FAQ */}
      <section style={{ marginTop: '2rem' }}>
        <h2>FAQ</h2>
        <p><b>Apa yang harus saya lakukan jika hak saya dilanggar?</b></p>
        <p>
          Jika hak Anda dilanggar, segera laporkan ke pihak sekolah atau konselor. Anda juga bisa
          mencari bantuan hukum melalui lembaga perlindungan anak atau instansi resmi terkait.
        </p>
      </section>
    </div>
  );
};

export default LegalCenter;
