import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import { FaClipboardList, FaComments, FaTachometerAlt, FaGavel, FaUsers } from 'react-icons/fa';

const navItems = [
  { to: '/pelaporan', label: 'Pelaporan', icon: <FaClipboardList /> },
  { to: '/counseling', label: 'Konseling', icon: <FaComments /> },
  { to: '/admin', label: 'Dashboard', icon: <FaTachometerAlt /> },
  { to: '/legal', label: 'Pusat Hukum', icon: <FaGavel /> },
  { to: '/team', label: 'Tim', icon: <FaUsers /> },
];

const Layout = ({ children }) => {
  const location = useLocation();

  return (
    <div style={{
      maxWidth: '480px',
      margin: '0 auto',
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      backgroundColor: 'white',
      color: '#4B3A26',
      fontFamily: 'Arial, sans-serif',
      boxShadow: '0 0 10px rgba(0,0,0,0.1)',
      borderRadius: '12px',
      overflow: 'hidden',
    }}>
      <main style={{ flex: 1, padding: '1rem', overflowY: 'auto' }}>
        {children}
      </main>
      <nav style={{
        display: 'flex',
        justifyContent: 'space-around',
        backgroundColor: '#4B3A26',
        padding: '0.5rem 0',
      }}>
        {navItems.map(({ to, label, icon }) => {
          const isActive = location.pathname === to;
          return (
            <NavLink
              key={to}
              to={to}
              style={{
                color: isActive ? 'white' : '#b0a68f',
                textDecoration: 'none',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                fontSize: '0.75rem',
                fontWeight: 'bold',
                transition: 'color 0.3s',
              }}
              end
            >
              <div style={{ fontSize: '1.5rem' }}>{icon}</div>
              {label}
            </NavLink>
          );
        })}
      </nav>
    </div>
  );
};

export default Layout;
