import React from 'react';
import { NavLink, useLocation } from 'react-router-dom';

const navItems = [
  { to: '/dashboard', label: 'Dashboard', icon: '🏠' },
  { to: '/pelaporan', label: 'Pelaporan', icon: '📝' },
  { to: '/counseling', label: 'Counseling', icon: '💬' },
  { to: '/legal', label: 'Legal Center', icon: '⚖️' },
  { to: '/team', label: 'Team', icon: '👥' },
];

const Layout = ({ children }) => {
  const location = useLocation();

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      backgroundColor: 'white',
      color: '#4B3A26',
      fontFamily: 'Arial, sans-serif',
      // Removed boxShadow and borderRadius for website look
    }}>
      <nav style={{
        display: 'flex',
        justifyContent: 'flex-start',
        backgroundColor: '#4B3A26',
        padding: '1rem',
        gap: '2rem',
      }}>
        {navItems.map(({ to, label, icon }) => {
          const isActive = location.pathname === to;
          return (
            <NavLink
              key={to}
              to={to}
              style={{
                color: isActive ? 'white' : '#b0a68f',
                textDecoration: 'none',
                display: 'flex',
                alignItems: 'center',
                fontSize: '1rem',
                fontWeight: 'bold',
                transition: 'color 0.3s',
                gap: '0.5rem',
              }}
              end
            >
              <div style={{ fontSize: '1.25rem' }}>{icon}</div>
              {label}
            </NavLink>
          );
        })}
      </nav>
      <main style={{ flex: 1, padding: '2rem', maxWidth: '960px', margin: '0 auto' }}>
        {children}
      </main>
    </div>
  );
};

export default Layout;
