import React from 'react';
import { useNavigate } from 'react-router-dom';

const OpeningPage = () => {
  const navigate = useNavigate();

  const handleEnter = () => {
    navigate('/pelaporan');
  };

  return (
    <div style={{
      height: '100vh',
      backgroundColor: 'white',
      color: '#4B3A26',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      fontFamily: 'Arial, sans-serif',
      textAlign: 'center',
      padding: '1rem',
    }}>
      <h1 style={{ fontSize: '3rem', margin: 0 }}>SELAMAT DATANG</h1>
      <h2 style={{ fontSize: '2rem', margin: '1rem 0' }}>SIGAP</h2>
      <button
        onClick={handleEnter}
        style={{
          backgroundColor: '#4B3A26',
          color: 'white',
          border: 'none',
          padding: '0.75rem 2rem',
          fontSize: '1.25rem',
          cursor: 'pointer',
          borderRadius: '5px',
        }}
      >
        Masuk
      </button>
    </div>
  );
};

export default OpeningPage;
